!(function (l) {
  "use strict";

  // Ensure Sidebar is Closed on Page Load for Mobile
  l(document).ready(function () {
    if (l(window).width() < 768) {
      l("body").addClass("sidebar-toggled");
      l(".sidebar").addClass("toggled");
      l(".sidebar .collapse").collapse("hide");
    }
  });

  // Toggle sidebar and update localStorage state
  l("#sidebarToggle, #sidebarToggleTop").on("click", function () {
    l("body").toggleClass("sidebar-toggled"),
      l(".sidebar").toggleClass("toggled"),
      l(".sidebar").hasClass("toggled") &&
        l(".sidebar .collapse").collapse("hide");
  });

  // Auto-close sidebar when resizing below 768px
  l(window).resize(function () {
    if (l(window).width() < 768) {
      l("body").addClass("sidebar-toggled");
      l(".sidebar").addClass("toggled");
      l(".sidebar .collapse").collapse("hide");
    }
  });

  // Prevent scrolling when hovering over fixed sidebar
  l("body.fixed-nav .sidebar").on("mousewheel DOMMouseScroll wheel", function (e) {
    if (l(window).width() > 768) {
      var o = e.originalEvent.wheelDelta || -e.originalEvent.detail;
      this.scrollTop += 30 * (o < 0 ? 1 : -1);
      e.preventDefault();
    }
  });

  // Scroll to top button visibility
  l(document).on("scroll", function () {
    l(this).scrollTop() > 100 ? l(".scroll-to-top").fadeIn() : l(".scroll-to-top").fadeOut();
  });

  // Smooth scrolling on "scroll-to-top" click
  l(document).on("click", "a.scroll-to-top", function (e) {
    var o = l(this);
    l("html, body").stop().animate(
      { scrollTop: l(o.attr("href")).offset().top },
      1e3,
      "easeInOutExpo"
    );
    e.preventDefault();
  });

})(jQuery);
